    it('should calculate sqrt (p = 5 mod 8)', () => {
      const primes = [];
      const squares = [];

      for (let i = 0; i < 10; i++) {
        let p;

        for (;;) {
          p = BN.randomBits(rng, 128);

          if (p.andln(7) !== 5)
            continue;

          if (p.isPrime(rng, 20))
            break;
        }

        primes.push(p);
      }

      for (let i = 0; i < 10; i++) {
        const p = primes[i];
        const items = [];

        for (let j = 0; j < 10; j++) {
          const x = BN.random(rng, 1, p);

          items.push(x.sqr().imod(p));
        }

        squares.push(items);
      }

      for (let i = 0; i < 10; i++) {
        const p = primes[i];
        const items = squares[i];
        const red = BN.red(p);

        assert(red.sm1 == null);

        for (let j = 0; j < 10; j++) {
          const x = items[j].toRed(red);
          const r = x.redSqrt();

          assert(r.redSqr().eq(x));
        }

        red.precompute();

        assert(red.sm1 != null);

        for (let j = 0; j < 10; j++) {
          const x = items[j].toRed(red);
          const r = x.redSqrt();

          assert(r.redSqr().eq(x));
        }
      }
    });

